SELECT v.video_id, v.channel_id, v.title, v.summary, v.thumbnail, cat.category, v.duration, v.seconds, v.origin, v.default_audio_language, v.default_language, v.wikitopics, v.published_at, v.retrieved_at, p.status, p.lang, p.lang_conf, cap.status as caption_status, cap.caption, cap.caption_type FROM video v JOIN pipeline p ON p.video_id = v.video_id LEFT JOIN caption cap on cap.video_id = v.video_id JOIN yt_categories cat ON cat.category_id = v.category_id WHERE p.status != 'incomplete' and v.published_at > now() - interval '6 weeks';
